import axios from "axios";
export default {
  name: "App",
  data: function data() {
    return {
      books: []
    };
  },
  // 请求图书，并渲染到页面上
  // 1.发送请求：baseURl +url
  // 2.服务端返回的数据，res.data
  // 3.请求封装成函数，在created中调用更显语意话
  created: function created() {
    this.getBooks();
  },
  methods: {
    getBooks: function getBooks() {
      var _this = this;

      axios({
        url: 'http://123.57.109.30:3006/api/getbooks'
      }).then(function (res) {
        _this.books = res.data.data;
      });
    }
  }
};
